export default {
  data() {
    return {
      title: '',
      description: '',
      type: 'single'
    };
  },
  methods: {
    submit() {
      const question = {
        title: this.title,
        description: this.description,
        type: this.type
      };
      this.$emit('addQuestion', question);
      this.title = '';
      this.description = '';
      this.type = 'single';
    }
  }
};
